import React from 'react';
import { View, Text, TouchableOpacity, TextInput, StyleSheet } from 'react-native';

const Calculatorno = () => {
  const buttons = [
    '7', '8', '9',
    '4', '5', '6',
    '1', '2', '3',
    'C', '0', '=',
    '/', '*', '-', '+'
  ];

  return (
    <View style={styles.container}>
      <TextInput
        style={styles.input}
        editable={false}
        placeholder="0"
      />
      <Text style={styles.result}>Result</Text>
      <View style={styles.buttonContainer}>
        {buttons.map((button, index) => (
          <TouchableOpacity
            key={index}
            style={styles.button}
          >
            <Text style={styles.buttonText}>{button}</Text>
          </TouchableOpacity>
        ))}
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  input: {
    borderWidth: 1,
    width: '100%',
    marginBottom: 10,
    padding: 5,
    textAlign: 'center',
  },
  result: {
    fontSize: 24,
    marginBottom: 10,
    textAlign: 'center',
  },
  buttonContainer: {
    flexDirection: 'row',
    flexWrap: 'wrap',
    justifyContent: 'center',
  },
  button: {
    width: '30%',
    padding: 10,
    alignItems: 'center',
    margin: 5,
  },
  buttonText: {
    fontSize: 18,
  },
});

export default Calculatorno;
